      }

      a {
        color: #fff;
      }
        a:hover,
        a:active {
          text-decoration: underline;
        }

      em {
        display: inline-block;
        padding: 0 5px;

        background: #f4f4f4;
        border-radius: 5px;

        font-style: normal;
        font-weight: bold;
        color: #444;
      }

      form {
        color: #444;
      }
        label {
          display: block;
          font-weight: bold;
        }

        input[type=text],
        input[type=password] {
          margin-bottom: 1em;
          padding: 0.4em;

          max-width: 330px;
          width: 100%;

          border: 1px solid #c4c4c4;
          border-radius: 5px;
          outline: 0;

          font-size: 1.2em;
        }
          input[type=text]:focus,
          input[type=password]:focus,
          input[type=text]:active,
